import{_ as s,o as n,c as a,d as l}from"./chunks/framework.4d89205b.js";const e=JSON.parse('{"title":"node版本管理工具","description":"","frontmatter":{},"headers":[{"level":2,"title":"nvm","slug":"nvm","link":"#nvm","children":[{"level":3,"title":"常用命令","slug":"常用命令","link":"#常用命令","children":[]}]},{"level":2,"title":"volta","slug":"volta","link":"#volta","children":[{"level":3,"title":"推荐的原因","slug":"推荐的原因","link":"#推荐的原因","children":[]},{"level":3,"title":"常用命令","slug":"常用命令-1","link":"#常用命令-1","children":[]}]},{"level":2,"title":"暴力删除","slug":"暴力删除","link":"#暴力删除","children":[]},{"level":2,"title":"参考资料","slug":"参考资料","link":"#参考资料","children":[]},{"level":2,"title":"node版本","slug":"node版本","link":"#node版本","children":[]},{"level":2,"title":"技巧","slug":"技巧","link":"#技巧","children":[]}],"relativePath":"front-end/node/辅助工具/node版本管理工具.md"}'),o={name:"front-end/node/辅助工具/node版本管理工具.md"},p=[l('<h1 id="node版本管理工具" tabindex="-1">node版本管理工具 <a class="header-anchor" href="#node版本管理工具" aria-label="Permalink to &quot;node版本管理工具&quot;">​</a></h1><p>介绍使用过的俩个，<code>nvm</code>、<code>volta</code></p><h2 id="nvm" tabindex="-1">nvm <a class="header-anchor" href="#nvm" aria-label="Permalink to &quot;nvm&quot;">​</a></h2><p><a href="https://github.com/coreybutler/nvm-windows/releases" target="_blank" rel="noreferrer">nvm-windows的github地址</a> ，node版本管理工具，在<code>window</code>上无法设置默认版本，但是在<code>mac</code>上，可以设置。</p><h3 id="常用命令" tabindex="-1">常用命令 <a class="header-anchor" href="#常用命令" aria-label="Permalink to &quot;常用命令&quot;">​</a></h3><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># 查看版本</span></span>\n<span class="line"><span style="color:#FFCB6B;">nvm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--version</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># 查看版本</span></span>\n<span class="line"><span style="color:#FFCB6B;">nvm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">list</span></span>\n<span class="line"><span style="color:#FFCB6B;">nvm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ls</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># 下载版本</span></span>\n<span class="line"><span style="color:#FFCB6B;">nvm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">xxx</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># 卸载版本</span></span>\n<span class="line"><span style="color:#FFCB6B;">nvm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">uninstall</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">xxx</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># 使用版本</span></span>\n<span class="line"><span style="color:#FFCB6B;">nvm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">use</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">xxx</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># 查看当前版本</span></span>\n<span class="line"><span style="color:#FFCB6B;">nvm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">current</span></span>\n<span class="line"><span style="color:#FFCB6B;">node</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-v</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># mac设置默认版本</span></span>\n<span class="line"><span style="color:#FFCB6B;">nvm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">alias</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">default</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">xxx</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># 查看api</span></span>\n<span class="line"><span style="color:#FFCB6B;">nvm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">help</span></span>\n<span class="line"></span></code></pre></div><h2 id="volta" tabindex="-1">volta <a class="header-anchor" href="#volta" aria-label="Permalink to &quot;volta&quot;">​</a></h2><p><a href="https://volta.sh/" target="_blank" rel="noreferrer">volta官方文档</a>，推荐使用该node版本管理工具，在<code>window</code>上可以设置默认版本。window系统，可以通过exe文件，下一步到底安装完成。</p><h3 id="推荐的原因" tabindex="-1">推荐的原因 <a class="header-anchor" href="#推荐的原因" aria-label="Permalink to &quot;推荐的原因&quot;">​</a></h3><p>跨平台支持、工具安装稳定。。。</p><h3 id="常用命令-1" tabindex="-1">常用命令 <a class="header-anchor" href="#常用命令-1" aria-label="Permalink to &quot;常用命令&quot;">​</a></h3><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># 查看版本</span></span>\n<span class="line"><span style="color:#FFCB6B;">volta</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--version</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># 将工具缓存到本地，方便使用，下次不用重新下载</span></span>\n<span class="line"><span style="color:#FFCB6B;">volta</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">fetch</span><span style="color:#A6ACCD;">       </span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># 下载一个工具例如 volta install node@14(自动下载稳定版本)   切换node版本也可以使用此命令</span></span>\n<span class="line"><span style="color:#FFCB6B;">volta</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;">   </span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># 卸载一个工具，指定详细名称如node/npm/cnpm/yarn   （暂不支持，只能暴力删除）</span></span>\n<span class="line"><span style="color:#FFCB6B;">volta</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">uninstall</span><span style="color:#A6ACCD;">    </span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># 将使用的工具版本固定到json中</span></span>\n<span class="line"><span style="color:#FFCB6B;">volta</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">pin</span><span style="color:#A6ACCD;">        </span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># 查看当前使用工具的版本,后面加上工具名称如node/npm/cnpm/yarn都可以  </span></span>\n<span class="line"><span style="color:#FFCB6B;">volta</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">list</span><span style="color:#A6ACCD;">       </span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># 不知道有啥用</span></span>\n<span class="line"><span style="color:#FFCB6B;">volta</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">completions</span><span style="color:#A6ACCD;">  </span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># 查看volta安装目录</span></span>\n<span class="line"><span style="color:#FFCB6B;">volta</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">which</span><span style="color:#A6ACCD;">   </span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># 切换当前使用的用户,没啥用     </span></span>\n<span class="line"><span style="color:#FFCB6B;">volta</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">setup</span><span style="color:#A6ACCD;">      </span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#676E95;font-style:italic;"># 没啥用</span></span>\n<span class="line"><span style="color:#FFCB6B;">volta</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">help</span><span style="color:#A6ACCD;">         </span></span>\n<span class="line"></span></code></pre></div><h2 id="暴力删除" tabindex="-1">暴力删除 <a class="header-anchor" href="#暴力删除" aria-label="Permalink to &quot;暴力删除&quot;">​</a></h2><p><a href="https://github.com/volta-cli/volta/issues/327" target="_blank" rel="noreferrer">Support volta uninstall for node and yarn </a></p><h2 id="参考资料" tabindex="-1">参考资料 <a class="header-anchor" href="#参考资料" aria-label="Permalink to &quot;参考资料&quot;">​</a></h2><p><a href="https://juejin.cn/post/7085383981100695565#heading-2" target="_blank" rel="noreferrer">还在用nvm做node管理工具？快来试试Volta吧！</a></p><h2 id="node版本" tabindex="-1">node版本 <a class="header-anchor" href="#node版本" aria-label="Permalink to &quot;node版本&quot;">​</a></h2><p>node版本，默认14，这是公认比较稳定的。</p><h2 id="技巧" tabindex="-1">技巧 <a class="header-anchor" href="#技巧" aria-label="Permalink to &quot;技巧&quot;">​</a></h2><p><a href="https://js-mark.com/%E5%89%8D%E7%AB%AF/%E4%BD%BF%E7%94%A8Volta%E8%BF%9B%E8%A1%8C%E7%89%88%E6%9C%AC%E7%AE%A1%E7%90%86/" target="_blank" rel="noreferrer">使用Volta进行版本管理</a></p><p><code>volta install &lt;package name&gt;</code> 安装 <code>tools</code> 时与网络有关系，有时会死活下载不下来（主要应该是国内网络环境的原因），可以将自己手动下载的压缩包，或者其他机器上已经使用 <code>volta</code> 安装过该工具所下载的压缩包（在 <code>%VOLTA_HOME%\\tools\\inventory\\</code> 目录中），拷贝到 <code>%VOLTA_HOME%\\tools\\inventory\\</code> 下对应的文件夹内，比如将 <code>node-v12.18.2-win-x64.zip</code> 复制到 <code>%VOLTA_HOME%\\tools\\inventory\\node\\</code> 目录下，然后再重新执行 <code>install</code> 命令。</p>',21)];const t=s(o,[["render",function(s,l,e,o,t,c){return n(),a("div",null,p)}]]);export{e as __pageData,t as default};
