import{_ as e,o as a,c as t,d as l}from"./chunks/framework.ad53f7b3.js";const r=JSON.parse('{"title":"高效学习","description":"","frontmatter":{},"headers":[{"level":2,"title":"从官网入手","slug":"从官网入手","link":"#从官网入手","children":[]},{"level":2,"title":"不要死磕","slug":"不要死磕","link":"#不要死磕","children":[]},{"level":2,"title":"多记笔记","slug":"多记笔记","link":"#多记笔记","children":[]},{"level":2,"title":"大致思路","slug":"大致思路","link":"#大致思路","children":[]}],"relativePath":"other/高效学习.md"}'),i={name:"other/高效学习.md"},o=[l('<h1 id="高效学习" tabindex="-1">高效学习 <a class="header-anchor" href="#高效学习" aria-label="Permalink to &quot;高效学习&quot;">​</a></h1><blockquote><p>仅代表个人观点</p></blockquote><p>在日常学习新技术的时候，会很苦恼，学不明白怎么办？上手太慢怎么办？如何快速掌握一门语言 or 框架 ？</p><p>针对以上问题，总结出来的，适合个人学习的。仅做记录参考。</p><h2 id="从官网入手" tabindex="-1">从官网入手 <a class="header-anchor" href="#从官网入手" aria-label="Permalink to &quot;从官网入手&quot;">​</a></h2><p>不要一上来就英文文档，尽量从中文文档入手，过渡到英文文档。过渡的原因，主要是英文文档一般比较全。</p><h2 id="不要死磕" tabindex="-1">不要死磕 <a class="header-anchor" href="#不要死磕" aria-label="Permalink to &quot;不要死磕&quot;">​</a></h2><p>不要死磕文档描述，都是人写的，更新不及时也正常。以案例 + 代码为主要了解手段，文档描述为辅助。</p><p>也不要死磕一个技术点，不懂的话，就标记下，先学会熟练使用，在深入了解。（主打的就是一个，舒适的学习，不要有负担，一口气学不成胖子）</p><h2 id="多记笔记" tabindex="-1">多记笔记 <a class="header-anchor" href="#多记笔记" aria-label="Permalink to &quot;多记笔记&quot;">​</a></h2><p>好记性，不如烂笔头。学习的过程，记得总结记录。然后就是实操上手，毕竟“此技无他，唯手熟尔”，最后在重复总结，形成自己的知识库。</p><h2 id="大致思路" tabindex="-1">大致思路 <a class="header-anchor" href="#大致思路" aria-label="Permalink to &quot;大致思路&quot;">​</a></h2><p>了解 -&gt; 单独起项目，熟悉官网案例（实操便于理解） -&gt; 总结复习（笔记）-&gt; 实操项目 -&gt; 总结复习（笔记） -&gt; 看大佬项目（进阶的关键）-&gt; 总结复习（笔记）-&gt; 学以致用</p>',13)];const h=e(i,[["render",function(e,l,r,i,h,n){return a(),t("div",null,o)}]]);export{r as __pageData,h as default};
